import axios from 'axios';
import {ACCESS_TOKEN, API_BASE_URL, APP_CLIENT, APP_PASS, PAGE_SIZE, CLIENT_TOKEN,GETMETHOD,POSTMETHOD,PUTMETHOD,DELETEMETHOD} from '../../constants';


const axiosRequest = options =>{
    let setHeader = {}
    if (options.useUserToken && localStorage.getItem(ACCESS_TOKEN)) {
        let tempHeaders = {Authorization:`Bearer ${localStorage.getItem(ACCESS_TOKEN)}`}
        setHeader = {...setHeader, ...tempHeaders}
    }

    if (options.useBasic) {
        let tempHeaders={Authorization:`${getBasicAuth(APP_CLIENT, APP_PASS)}`}
        setHeader = {...setHeader, ...tempHeaders}
        // headers.set('Content-Type', 'application/x-www-form-urlencoded;charset=UTF-8');
    }

    if(options.useClientToken){
        let tempHeaders={Authorization:`Bearer ${getClientToken()}`}
        setHeader = {...setHeader, ...tempHeaders}
    }

    options.headerRequest = {headers:setHeader}

    let apiRequest = null;

    switch (options.method) {
        case GETMETHOD:
            apiRequest = axios.get(options.url,options.headerRequest)
        break;

        case POSTMETHOD:
            apiRequest= axios.post(options.url, options.data, options.headerRequest);
        break;

        case PUTMETHOD:
            apiRequest= axios.put(options.url, options.data,options.headerRequest);
        break;

        default:
          apiRequest = null;
    }

    return apiRequest;

}

export function login(loginRequest) {
    loginRequest.grant_type = 'password';
    loginRequest.scope = 'profile';

    let formBody = [];
    for (let property in loginRequest) {
        let encodedKey = encodeURIComponent(property);
        let encodedValue = encodeURIComponent(loginRequest[property]);
        formBody.push(encodedKey + "=" + encodedValue);
    }
    formBody = formBody.join("&");

    return request({
        url: API_BASE_URL + '/oauth/token',
        method: POSTMETHOD,
        body: formBody,
        useBasic: true
    });
} 

export const  getBasicAuthentication =()=>{
    // const options =  
    // { headers:
    //      {'Content-Type': 'application/json', 'Authorization': `${getBasicAuth(APP_CLIENT, APP_PASS)}` } 
    // };
    const formData = new FormData();
    formData.append("grant_type","client_credentials")
    // let data = {"grant_type":"client_credentials"}
    // this doesnt work
    // return axios.post(`${API_BASE_URL}/oauth/token`,formData, options)

    return axiosRequest({
        useBasic: true,
        data: formData,
        url: `${API_BASE_URL}/oauth/token`,
        method: POSTMETHOD
    });
}

const getBasicAuth = (username, password) => {
    const hash = new Buffer(username + ':' + password).toString('base64');
    console.log(hash);
    return "Basic " + hash;
};
