import React, { Component } from 'react';
import Header from '../../../partials/header';
import Navbar from '../../../partials/navbar'
import BreadCrumb from '../../../partials/breadcrumb'
import {fetchOneUser,fetchOneUserAuditTrail,fetchAllroles} from './userinfo-api'
import {
    Form,
    Input,
    Divider,
    Icon,Card, Avatar,Checkbox,
    Spin,
    notification,Popconfirm
  } from 'antd';
  import AuditTrailTable from '../../../common/AuditTableComponent'


const { Meta } = Card;

class UserInfoPage extends Component  {
    constructor(props) {
        super(props);
        this.state={
            systemRoles:[],
            userId:null,
            auditTrails:[],
            isloading:true,
            isUserloading:true,
            checkBoxModal:{
                title:'',
                roles:[]
            },
            visible:false
        }

    }

    componentDidMount(){
        const {match: { params }} = this.props;
        this.setState({userId:params.userId})
        this.getSingleUser(params.userId)
        this.getSingleUserAuditTrails(params.userId)
        this.getAllRoles()
    }



    getAllRoles(){
        this.setState({isUserloading:true})
        fetchAllroles()
        .then(response=>{
            console.log("Roles Response");
            console.log(response.data)
            this.setState({ussystemRoleser:response.data})
            this.setState({isUserloading:false})
        })
        .catch((error)=> {
            console.log("Roles Error Response");

            notification['error']({
            message: 'MEDTHREAT',
            description:
                `An Error Occured Fetching Roles .`,
            });

            this.setState({isUserloading:false})
            console.log(error);

        });
    }

    getSingleUser = (userId) =>{
        this.setState({isUserloading:true})
        fetchOneUser(userId)
        .then(response=>{
            console.log("Users Response");
            console.log(response.data)
            this.setState({user:response.data})
            this.setState({isUserloading:false})
        })
        .catch((error)=> {
            console.log("User Error Response");

            notification['error']({
            message: 'MEDTHREAT',
            description:
                `An Error Fetching User .`,
            });

            this.setState({isUserloading:false})
            console.log(error);

        });
    }

    getSingleUserAuditTrails = (userId) =>{
        this.setState({isloading:true})
        fetchOneUserAuditTrail(userId)
        .then(response=>{
            console.log("Audit one Response");
            console.log(response.data)
            this.setState({auditTrails:response.data})
            this.setState({isloading:false})
        })
        .catch((error)=> {
            console.log("Audit Error Response");

            notification['error']({
            message: 'MEDTHREAT',
            description:
                `An Error Fetching Audit Trail .`,
            });

            this.setState({isloading:false})
            console.log(error);

        });
    }

    getAccontLockedInfo = (block)=>{
        let badge = {clazz:"badge badge-info", display:"Active"}
        if(block==1){
            badge = {clazz:"badge badge-danger", display:"Blocked"}
        }    
        return (
            <span className={badge.clazz}>
                {badge.display}
            </span>
        );                  
    }

    onSelectRole = (role)=>{
        console.log('checked = ', checkedValues);

    }

    deleteDrug = (id)=>{
        console.log( id)

    }
    cancelDrug = ()=>{

    }

    mapDrugTables = () =>{
        let items = <tr style={{ width: '100%' }} className="col-md-12" >No drug</tr>

        if (this.state.drugs) {

            items = this.state.drugs.map(drug => {
              return (
                <tr key={drug.id}>
                    <td>{drug.name}</td>
                    <td>{drug.amount}</td>
                    <td>{drug.alias}</td>
                    <td>
                    <Popconfirm
                        title="Are you sure delete this Item?"
                        onConfirm={(e)=>this.deleteDrug(drug)}
                        onCancel={this.cancelDrug()}
                        okText="Yes"
                        cancelText="No"
                    >
                        <a href="#" style={{margin:'5px'}} >Delete</a>
                        {/* <a style={{margin:'5px'}} type="button" class="btn btn-primary">Update</a>     */}
                    </Popconfirm>
                        <button type="button" class="btn btn-primary btn-sm">Delete</button>

                            {/* <Button className='m-1' color="primary" size="xs"
                                onClick={() => { this.setPublicationUpdate(item) }}>
                                Update
                            </Button> */}
                            {/* <Button className='m-1' color="danger" size="xs"
                                onClick={() => { this.deleteItem(item) }}>
                                Delete
                            </Button> */}
                    </td>  
                </tr>
              )
            });
        }

        return items;

    }

    test=()=>{
        console.log("lol")
    }

  render() {
    const bodystyle =  {
        background: "#f1f2f7",
        display: "table",
        fontFamily: "'Open Sans' sans-serif !important",
        fontSize: "16px",
        width: "100%" }

    const {user}=this.state
    const plainOptions = ['Apple', 'Pear', 'Orange'];


    return (
        <div style={bodystyle}>
        {/* <!-- Left Panel --> */}

            <Navbar></Navbar>
            {/* <!-- /#left-panel --> */}
            {/* <!-- Left Panel --> */}

            {/* <!-- Right Panel --> */}

            <div  class="right-panel ">
                <Header></Header>
                {/* <!-- /header --> */}
                {/* <!-- Header--> */}

                <BreadCrumb menu="User" submenu="User Information"></BreadCrumb>

                <div class="content mt-3">

                    <div class="animated fadeIn">
                        <div className="row">
                            <Modal
                            title="ADD ROLES"
                            visible={this.state.visible}
                            onOk={this.updateDrug}
                            okButtonProps={{ disabled: this.isFormInvalid() }}
                            onCancel={this.cancelDrugModal}
                            okText="UPDATE"
                            >
                            <Checkbox.Group options={plainOptions} onChange={this.onSelectRole} />

                            </Modal>
                        </div>
                        <Spin spinning={this.state.isUserloading}>
                            <div class="row">

                                <div className="col-md-12">
                                    <Card style={{ width: "100%", marginTop: 16 }}
                                        actions={[
                                            <a onClick={()=>{this.test()}}><Icon type="plus-circle" /></a>,
                                            <a><Icon type="minus-circle" key="edit" /></a>,
                                            <Icon type="ellipsis" key="ellipsis" />,
                                        ]} >
                                        <Meta
                                            avatar={
                                                <Avatar size="large" icon="user" />
                                            }
                                            title="User details"
                                            description="User description"
                                        />
                                        <p><b>FullName</b>: {user.firstName} {user.lastName}</p>
                                        <p><b>Phone Number </b>: {user.phoneNo}</p>
                                        <p><b>Email</b>: {user.email}</p>
                                        <p><b> UnAuthorized Activity Count block</b>: {user.illegalCount}</p>
                                        <p><b> Account Status</b>:  {this.getAccontLockedInfo(user.block)}</p>
                                        <p>
                                            <b>Roles:</b>
                                            {user.roles.map(role => {
                                                return (
                                                    <span>
                                                        <span className="badge badge-secondary">
                                                            {role.name}

                                                        </span>
                                                        <Divider type="vertical" />
                                                    </span>

                                                );   
                                            })}

                                        </p>

                                    </Card>

                                </div>

                            </div>
                        </Spin>

                        <Spin spinning={this.state.isloading}>

                            {/* Table for drugs */}
                            <div class="row">

                                <div className="col-md-12">
                                    <AuditTrailTable auditTrails={this.state.auditTrails}></AuditTrailTable>

                                    {/* <div className="card">
                                        <div className="card-header">
                                            <strong className="card-title">Data Table</strong>
                                        </div>
                                        <div className="card-body">
                                            <Table columns={this.getTableHeader()} dataSource={this.state.users} />
                                        </div>

                                    </div> */}
                                </div>


                            </div>

                        </Spin>

                    </div>
                </div>
                {/* <!-- .content --> */}
            </div>
            {/* <!-- /#right-panel --> */}

            {/* <!-- Right Panel --> */}
        </div>
    );
  }
}

export default UserInfoPage;
