import React, { Component } from 'react';
import Header from '../../../partials/header';
import Navbar from '../../../partials/navbar'
import BreadCrumb from '../../../partials/breadcrumb'
import {fetchAllDrugs} from './drug-api'
import {
    Form,
    Input,
    Tooltip,
    Icon,
    Cascader,
    Select,
    Row,
    Col,
    Checkbox,
    Button,
    AutoComplete,
    Spin,
  } from 'antd';

const FormItem = Form.Item;

class DrugPage extends Component {
    render() {
        const AntWrappedDrug = Form.create()(Drug);
        return (
            <AntWrappedDrug />           
        );
    }
}

class Drug extends Component  {
    constructor(props) {
        super(props);
        this.state={
            drugs:null,
            isloading:true
        }
            // this.onSubmitExperience = this.onSubmitExperience.bind(this);

    }

    componentDidMount(){
        this.getAllDrugs()
    }

    onSubmitExperience = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                const loginRequest = Object.assign({}, values);
                console.log(values);
                let loginRequests=values;
                console.log(loginRequest);
                loginRequest.data={};
                console.log(loginRequest);
                const logReq = {...values};
                console.log(logReq);

            }
        });
    }

    getAllDrugs = () =>{
        this.setState({isloading:true})
        fetchAllDrugs()
        .then(response=>{
            console.log(response.data)
            this.setState({drugs:response.data})
            this.setState({isloading:false})
        })
    }

    mapDrugTables = () =>{
        let items = <tr style={{ width: '100%' }} className="col-md-12" >No drug</tr>

        if (this.state.drugs.length > 0) {

            items = this.state.drugs.map(drug => {
              return (
                // <option key={item.id} value={item.id}> {item.name} </option>  
                <tr key={drug.id}>
                    <td>{drug.name}</td>
                    <td>{drug.amount}</td>
                    <td>{drug.alias}</td>
                    <td>
                        <Button className='m-1' color="primary" size="xs"
                            onClick={() => { this.setPublicationUpdate(item) }}>
                            Update
                        </Button>
                        <Button className='m-1' color="danger" size="xs"
                            onClick={() => { this.deleteItem(item) }}>
                            Delete
                        </Button>
                    </td>  
                </tr>
              )
            });
        }

    }

  render() {
    const {getFieldDecorator} = this.props.form;
    const bodystyle =  {
        background: "#f1f2f7",
        display: "table",
        fontFamily: "'Open Sans' sans-serif !important",
        fontSize: "16px",
        width: "100%" }

    return (
        <div style={bodystyle}>
        {/* <!-- Left Panel --> */}

            <Navbar></Navbar>
            {/* <!-- /#left-panel --> */}
            {/* <!-- Left Panel --> */}

            {/* <!-- Right Panel --> */}

            <div  class="right-panel ">
                <Header></Header>
                {/* <!-- /header --> */}
                {/* <!-- Header--> */}

                <BreadCrumb></BreadCrumb>

                <div class="content mt-3">

                    <div class="animated fadeIn">
                        <div className="row">
                            <div class="col-lg-9">
                                <div class="card">
                                    <div class="card-header">
                                        <strong>Drug</strong>
                                        <small> Form</small>
                                    </div>
                                    <form onSubmit={this.onSubmitExperience}>
                                        <div class="card-body card-block">
                                            <div class="form-group">
                                                <FormItem>
                                                    <label  class=" form-control-label">
                                                        Name
                                                    </label>
                                                    {getFieldDecorator('password', {
                                                        rules: [
                                                            {
                                                                required: true,
                                                                message: 'Please input your Name!'
                                                            }
                                                        ]
                                                    })(
                                                        <Input
                                                            prefix={<Icon type="lock"/>}
                                                            name="name"
                                                            placeholder="Enter your Drug name"
                                                        />

                                                    )}
                                                </FormItem>    
                                            </div>
                                            <div class="form-group">
                                                <FormItem>
                                                    <label for="company" class=" form-control-label">
                                                        Amount
                                                    </label>
                                                    {getFieldDecorator('amount', {
                                                        rules: [
                                                            {
                                                                required: true,
                                                                message: 'Please input Amount!'
                                                            }
                                                        ]
                                                    })(
                                                        <Input
                                                            prefix={<Icon type="lock"/>}
                                                            name="amount"
                                                            placeholder="Enter your Amount"
                                                        />

                                                    )}
                                                </FormItem>    
                                            </div>

                                            <div class="form-group">
                                                <FormItem>
                                                    <label for="company" class=" form-control-label">
                                                        Alias
                                                    </label>
                                                    {getFieldDecorator('alias', {
                                                        rules: [
                                                            {
                                                                required: true,
                                                                message: 'Please input Alias !'
                                                            }
                                                        ]
                                                    })(
                                                        <Input
                                                            prefix={<Icon type="lock"/>}
                                                            name="alias"
                                                            placeholder="Enter your Drug Alias"
                                                        />

                                                    )}
                                                </FormItem>    
                                            </div>

                                        </div>
                                    </form>
                                </div>

                            </div>
                        </div>

                        {/* Table for drugs */}
                        <div class="row">

                        <div className="col-md-12">
                            <div className="card">
                                <div className="card-header">
                                    <strong className="card-title">Data Table</strong>
                                </div>
                                <div className="card-body">
                                    <table id="bootstrap-data-table-export" className="table table-striped table-bordered">
                                        <thead>
                                            <tr>
                                                <th>Name</th>
                                                <th>Position</th>
                                                <th>Office</th>
                                                <th>Salary</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            <tr>
