            console.log("drug Response");
            console.log(response.data)
            this.setState({auditTrails:response.data})
            this.setState({isloading:false})
        })
        .catch((error)=> {
            console.log("drug Error Response");

            notification['error']({
            message: 'MEDTHREAT',
            description:
                `An Error Fetching Drug .`,
            });

            console.log(error.response.status);

        });
    }

    deleteDrug = (id)=>{
        console.log( id)

    }
    cancelDrug = ()=>{

    }

    mapDrugTables = () =>{
        let items = <tr style={{ width: '100%' }} className="col-md-12" >No drug</tr>

        if (this.state.drugs) {

            items = this.state.drugs.map(drug => {
              return (
                <tr key={drug.id}>
