import React, { Component } from 'react';
import Header from '../../../partials/header';
import Navbar from '../../../partials/navbar'
import BreadCrumb from '../../../partials/breadcrumb'
import {fetchAllAuditTrails} from './auditTrails-api'
import {Spin,notification,Popconfirm,Table, Divider, Tag} from 'antd';
import AuditTrailTable from '../../../common/AuditTableComponent'

class AuditTrailPage extends Component  {
    constructor(props) {
        super(props);
        this.state={
            auditTrails:[],
            isloading:true
        }
            // this.onsubmitDrug = this.onsubmitDrug.bind(this);
            // this.deleteDrug = this.deleteDrug.bind(this);

    }

    componentDidMount(){

        this.getAllAuditTrails()
    }


    getLegalStatusTag = (legal_status)=>{
        let clazzName = "badge badge-primary"
        if(legal_status=="LEGAL"){
            clazzName="badge badge-success"
        }
        else if(legal_status=="ILLEGAL"){
            clazzName="badge badge-danger"
        }

        return (
            <span className={clazzName}>
                {legal_status}
            </span>
        );


    }

    formatUserForTableDisplay=(user)=>{
        let fullname =""
        if(user != null ){
            fullname="dfg"
        //    fullname = `${user.firstName} ${user.lastName}` ;
        }

        return(
            <span>

                   {fullname}

            </span>
        )

    }
    getTableHeader = ()=>{
        const columns = [
            {
              title: 'Fuller Name',
              dataIndex: 'user',
              key: 'user',
              render: user => (
                 this.formatUserForTableDisplay(user)
              )
            },
            {
              title: 'Email',
              dataIndex: 'email',
              key: 'email',
            },
            {
              title: 'Request',
              dataIndex: 'request',
              key: 'request',
            },
            {
                title: 'Target Url',
                dataIndex: 'target_url',
                key: 'target_url',
            },

            {
                title: 'ip',
                dataIndex: 'ip',
                key: 'ip',
            },

            {
                title: 'Legal Status',
                dataIndex: 'legal_status',
                key: 'legal_status',
                render: legal_status => (
                    <span>
                        {this.getLegalStatusTag(legal_status)}


                    </span>
                  ),
            },

            {
                title: 'Action',
                dataIndex: 'action',
                key: 'action',
            },
          ];

          return columns
    }


    // onsubmitDrug = (e) => {
    //     e.preventDefault();
    //     this.setState({isloading:true})
    //     this.props.form.validateFields((err, values) => {
    //         if (!err) {
    //             console.log(values);
    //             createDrug(values)
    //             .then(response =>{
    //                 console.log(response.data);
    //                 if(response.data){
    //                     notification['success']({
    //                         message: 'MEDTHREAT',
    //                         description:
    //                           'Drug Created Successfully',
    //                       });
    //                     this.props.form.resetFields();
    //                     this.getAllDrugs()
    //                 }
    //             })
    //             .catch((error)=> {
    //                 console.log("Drug Error Response");

    //                 notification['error']({
    //                 message: 'MEDTHREAT',
    //                 description:
    //                     `Error Occured Creating Drug .`,
    //                 });
    //                 this.setState({isloading:false})
    //                 console.log(error.response.status);

    //             });
    //         }
    //     });
    // }

    getAllAuditTrails = () =>{
        this.setState({isloading:true})
        fetchAllAuditTrails()
        .then(response=>{
            console.log("drug Response");
            console.log(response.data)
            this.setState({auditTrails:response.data})
            this.setState({isloading:false})
        })
        .catch((error)=> {
            console.log("drug Error Response");

            notification['error']({
            message: 'MEDTHREAT',
            description:
                `An Error Fetching Drug .`,
            });

            console.log(error.response);

        });
    }

    deleteDrug = (id)=>{
        console.log( id)

    }
    cancelDrug = ()=>{

    }

    mapDrugTables = () =>{
        let items = <tr style={{ width: '100%' }} className="col-md-12" >No drug</tr>

        if (this.state.drugs) {

            items = this.state.drugs.map(drug => {
              return (
                <tr key={drug.id}>
                    <td>{drug.name}</td>
                    <td>{drug.amount}</td>
                    <td>{drug.alias}</td>
                    <td>
                    <Popconfirm
                        title="Are you sure delete this Item?"
                        onConfirm={(e)=>this.deleteDrug(drug)}
                        onCancel={this.cancelDrug()}
                        okText="Yes"
                        cancelText="No"
                    >
                        <a href="#" style={{margin:'5px'}} >Delete</a>
                        {/* <a style={{margin:'5px'}} type="button" class="btn btn-primary">Update</a>     */}
                    </Popconfirm>
                        <button type="button" class="btn btn-primary btn-sm">Delete</button>

                            {/* <Button className='m-1' color="primary" size="xs"
                                onClick={() => { this.setPublicationUpdate(item) }}>
                                Update
                            </Button> */}
                            {/* <Button className='m-1' color="danger" size="xs"
                                onClick={() => { this.deleteItem(item) }}>
                                Delete
                            </Button> */}
                    </td>  
                </tr>
              )
            });
        }

        return items;

    }

  render() {
    const bodystyle =  {
        background: "#f1f2f7",
        display: "table",
        fontFamily: "'Open Sans' sans-serif !important",
        fontSize: "16px",
        width: "100%" }

    return (
        <div style={bodystyle}>
        {/* <!-- Left Panel --> */}

            <Navbar></Navbar>
            {/* <!-- /#left-panel --> */}
            {/* <!-- Left Panel --> */}

            {/* <!-- Right Panel --> */}

            <div  class="right-panel ">
                <Header></Header>
                {/* <!-- /header --> */}
                {/* <!-- Header--> */}

                <BreadCrumb menu="Audit Trail" submenu=" "></BreadCrumb>

                <div class="content mt-3">

                    <div class="animated fadeIn">
                        <Spin spinning={this.state.isloading}>

                            {/* Table for drugs */}
                            <div class="row">

                            <div className="col-md-12">

                                <AuditTrailTable auditTrails={this.state.auditTrails}></AuditTrailTable>

                                {/* <div className="card">
                                    <div className="card-header">
                                        <strong className="card-title">Data Table</strong>
                                    </div>
                                    <div className="card-body">
                                        <Table columns={this.getTableHeader()} dataSource={this.state.auditTrails} />
                                    </div>

                                </div> */}
                            </div>


                        </div>

                        </Spin>

                    </div>
                </div>
                {/* <!-- .content --> */}
            </div>
            {/* <!-- /#right-panel --> */}

            {/* <!-- Right Panel --> */}
        </div>
    );
  }
}

export default AuditTrailPage;
