import React, { Component } from 'react';
import Header from '../../../partials/header';
import Navbar from '../../../partials/navbar'
import BreadCrumb from '../../../partials/breadcrumb'
import {fetchAllDrugs,createDrug,updateDeleteDrug} from './drug-api'
import {
    Form,
    Input,
    Tooltip,
    Icon,
    Cascader,
    Select,
    Row,
    Col,
    Checkbox,
    Button,
    Modal,
    Spin,
    notification,
    Popconfirm
  } from 'antd';

const FormItem = Form.Item;

class DrugPage extends Component {
    render() {
        const AntWrappedDrug = Form.create()(Drug);
        return (
            <AntWrappedDrug />           
        );
    }
}

class Drug extends Component  {
    constructor(props) {
        super(props);
        this.state={
            drugs:[],
            isloading:true,
            drug:{},
            visible:false,

            name: {
                value: ''
            },
            amount: {
                value: ''
            },
            alias: {
                value: ''
            },

        }
            this.onsubmitDrug = this.onsubmitDrug.bind(this);
            this.deleteDrug = this.deleteDrug.bind(this);

    }

    componentDidMount(){
        this.getAllDrugs()
    }

    onsubmitDrug = (e) => {
        e.preventDefault();
        this.setState({isloading:true})
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log(values);
                createDrug(values)
                .then(response =>{
                    console.log(response.data);
                    if(response.data){
                        notification['success']({
                            message: 'MEDTHREAT',
                            description:
                              'Drug Created Successfully',
                          });
                        this.props.form.resetFields();
                        this.getAllDrugs()
                    }
                })
                .catch((error)=> {
                    console.log("Drug Error Response");

                    notification['error']({
                    message: 'MEDTHREAT',
                    description:
                        `Error Occured Creating Drug .`,
                    });
                    this.setState({isloading:false})
                    console.log(error.response.status);

                });
            }
        });
    }

    getAllDrugs = () =>{
        this.setState({isloading:true})
        fetchAllDrugs()
        .then(response=>{
            console.log("drug Response");
            console.log(response.data)
            this.setState({drugs:response.data})
            this.setState({isloading:false})
        })
        .catch((error)=> {
            console.log("drug Error Response");

            notification['error']({
            message: 'MEDTHREAT',
            description:
                `An Error Fetching Drug .`,
            });

            console.log(error.response.status);

        });
    }

    deleteDrug = (id)=>{
        console.log( id)
        updateDeleteDrug(id)
        .then(response=>{
            console.log("drug Response");
            console.log(response.data)
            this.getAllDrugs()

            // this.setState({drugs:response.data})
            // this.setState({isloading:false})
        })
        .catch((error)=> {
            console.log("drug Error Response");

            notification['error']({
            message: 'MEDTHREAT',
            description:
                `An Error Occured Deleting Drug .`,
            });

            console.log(error);

        });
    }

    enableDrugModal =(drug)=>{
        this.setState({drug})
        this.setState({visible:true})
    }

    cancelDrug = ()=>{

    }

    validateInput = (input) => {
        if (input) {
            return {
                validateStatus: 'success',
                errorMsg: null,
            };
        } else {

            return {
                validateStatus: 'error',
                errorMsg: `This field is required`
            };
        }
    }

    handleInputChange(event, validationFun) {
        const target = event.target;
        const inputName = target.name;
        const inputValue = target.value;

        this.setState({
            [inputName]: {
                value: inputValue,
                ...validationFun(inputValue)
            }
        });
    }

    mapDrugTables = () =>{
        let items = <tr style={{ width: '100%' }} className="col-md-12" >No drug</tr>

        if (this.state.drugs) {

            items = this.state.drugs.map(drug => {
              return (
                <tr key={drug.id}>
                    <td>{drug.name}</td>
                    <td>{drug.amount}</td>
                    <td>{drug.alias}</td>
                    <td>
                    <Popconfirm
                        title="Are you sure delete this Item?"
