import axios from 'axios';
import {API_BASE_URL, APP_CLIENT, APP_PASS, PAGE_SIZE, CLIENT_TOKEN,GETMETHOD,POSTMETHOD,PUTMETHOD,DELETEMETHOD} from '../../constants';
import {axiosRequest} from  '../../utils/api-utils'



export function login(loginRequest) {
    loginRequest.grant_type = 'password';
    loginRequest.scope = 'profile';

    let formBody = [];
    for (let property in loginRequest) {
        let encodedKey = encodeURIComponent(property);
        let encodedValue = encodeURIComponent(loginRequest[property]);
        formBody.push(encodedKey + "=" + encodedValue);
    }
    formBody = formBody.join("&");

    return axiosRequest({
        url: API_BASE_URL + '/oauth/token',
        method: POSTMETHOD,
        body: formBody,
        useBasic: true
    });
} 


const getBasicAuth = (username, password) => {
    const hash = new Buffer(username + ':' + password).toString('base64');
    console.log(hash);
    return "Basic " + hash;
};
